package com.class3;

public class ConcatAndOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=5;
	    int y=7;
	    
	    String a="Omer";
	    String b="name";
	    
	    System.out.println(x+y+a+b); //12Omername;
	    System.out.println(x+a+y+b); //5Omer7name;
	    System.out.println(x+a+b+y); //5Omername7;
	    System.out.println(a+b+x+y); //Omername12;
	    
	    
	    
	  
	  
	  
		
		
		
		
		
	}

}
