package com.class8;

public class Task20To50 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	for (int i=20;i<=50;i+=2) {
		System.out.println(i);
	}
	
	//2 way 
	for(int i=20;i<=50; i++ ) {
		if(i%2==0) {
			System.out.println(i);
		}
	
	}
	//print odd numbers between 20 and 50 
	for(int a=20 ;a<=50; a++) {
	if(a%2==1) {
		System.out.println(a);
	}
	}
	
	
	
	}

}
